    private void assertWalk(ArrayList<Integer[]> expectedWalk) {
        Assert.assertArrayEquals(expectedWalk.toArray(), actualDraw.toArray());
        Assert.assertArrayEquals(expectedSolution, actualSolution);
    }

    private void assertReachable(boolean expected) {
        Assert.assertEquals(expected, actualReachable);
    }

    private ArrayList<Integer[]> buildWalk(int... input) {
        ArrayList<Integer[]> out = new ArrayList<>();
        for (int i = 0; i < input.length; i += 2) {
            out.add(new Integer[]{input[i], input[i + 1]});
            expectedSolution[input[i]][input[i + 1]] = true;
        }
        return out;
    }

    private boolean[][] buildEmpty(int width, int height) {
        expectedSolution = new boolean[width][height];
        boolean[][] out = new boolean[width][height];
        for (int x = 0; x < width; x++)
            for (int y = 0; y < out[x].length; y++)
                if (x == 0 || x == width - 1 || y == 0 || y == height - 1)
                    out[x][y] = true;
        return setStartEnd(out);
    }

    private boolean[][] setStartEnd(boolean[][] field) {
        int width = field.length;
        int height = field[0].length;
        field[1][0] = false;
        field[width - 1][height - 2] = false;
        return field;
    }

